	position: relative;
	width: 12em;
	height: 12em;
	opacity:1
}

body.no-scroll {
    overflow: hidden; /* Prevent scrolling */
    height: 100%; /* Ensure the height is 100% to cover content */
}

.preloader {
    position: relative; /* Set position to relative for absolute children */
    width: 100%; /* Full width */
	min-height: 100vh; /* Full width */

    display: flex; /* Use flexbox */
    justify-content: center; /* Center horizontally */
    align-items: center; /* Center vertically */
}

.bm-pl.loaded{
	opacity:0;
	transition: opacity 1.5s;
}
.bm-pl__blob {
	animation: moveR 8s linear infinite;
	border-radius: 50%;
	mix-blend-mode: screen;
	position: absolute;
	top: 0;
	left: 0;
	width: 9em;
	height: 9em;
}
.bm-pl__blob--r {
	background: hsl(3,90%,55%);
}
