		border-radius: 74% 85% 63% 87% / 81% 100% 62% 82%;
		transform: translate(1em,2em) rotate(0) scale(0.75,0.875);
	}
}
@keyframes moveB {
	from, to {
		border-radius: 52% 59% 50% 58% / 56% 80% 40% 59%;
		transform: translate(1.75em,3em) rotate(0) scale(1,1);
	}
