var mouseDot;
// var linkDot;
var xoff,yoff;
function setup(){
  var cnv=createCanvas(window.innerWidth,window.innerHeight);
  cnv.parent('artboard');

  mouseDot=new MouseDot(createVector(0,0));

  xoff=random(1000);
  yoff=random(1000);

  linkDot=document.getElementById("link");
}

function draw(){
  background("#fafafa");

  mouseDot.update();
  mouseDot.display();

  linkDot.update();
  linkDot.display();
  var nScale=60;
